import { useParams, useNavigate } from 'react-router-dom';
import useFetch from './useFetch';


const BlogDetails = () => {
    const { id } = useParams();
    const { data: recipe, error, isPending } =  useFetch('http://localhost:8000/blogs/' + id);
    const history = useNavigate();

    const handleClick = () => {
        fetch('http://localhost:8000/blogs/'+ recipe.id, {
            method: 'DELETE'
        }).then(() => {
            history.push('/');
        })
        window.location = '/';
    }
   // var filename = "\"../public/images/" + recipe.image.replace(/^.*?([^\\]*)$/, '$1') + "\"";
    //document.getElementById("content").innerHTML = `<img src='${filename}'>`;

    return ( 
        <div className="blog-details">
            {isPending && <div>Loading...</div>}
            {error && <div>{error}</div>}


            <img src={require('./public/images/spaghett.jpeg')} alt="" height="200px"></img>
            <br/>

            <h2>{recipe.title}</h2>

            <div>Time: {recipe.time} min</div>
            <div>Description: {recipe.description}</div>
            <div>Ingredients: {recipe.ingredients}</div>
            <div>Directions: {recipe.directions}</div>
            <button onClick={handleClick}>Delete</button>

        </div>
     );
}

export default BlogDetails;
