import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Create = () => {
    const [image, setImage] = useState('');
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [ingredient, setIngredient] = useState('');
    const [author, setAuthor] = useState('');
    const [isPending, setIsPending] = useState(false);
    const history = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        const blog = { title, description, author };

        setIsPending(true);

        fetch('http://localhost:8000/blogs', {
            method: 'POST',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(blog)
        }).then(() => {
            setIsPending(false);
            history.push('/');
        })
        window.location = '/';
    }
///////////////////////////////////////////////////////////////////////////////////

    function showPreview(event) {
        if (event.target.files.length > 0) {
        var src = URL.createObjectURL(event.target.files[0]);
        var preview = document.getElementById('bookCoverPreview');
        preview.src = src;
        preview.style.display = 'block';
        }
    }

/////////////////////////////////////////////////////////////////////////////////// 
    return ( 
        <div className="create">
            <h2>Add a New Recipe</h2>

            <form >
                <img src={bookCover} id="bookCoverPreview" alt="" />
                <input
                    type="file"
                    id="imgBtn"
                    accept="image/*"
                    onChange={showPreview}
                />
                <label>Recipe Title</label>
                <input 
                    type="text"
                    required
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <label>Description</label>
                <textarea
                    required
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />

                <label>Ingredients</label>
                <input
                    type="text"
                    required
                    value={ingredient}
                    onChange={(e) => setIngredient(e.target.value)}
                    />

                <label>Directions</label>

                <label>Notes</label>

                <label>Blog author:</label>
                <select
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                >
                    <option value="Shubham">Shubham</option>
                    <option value="Satyam">Satyam</option>
                    <option value="Anmol">Anmol</option>
                </select>

                {!isPending && <button onClick={handleSubmit}>Add Blog</button>}
                {isPending && <button onClick={handleSubmit} disabled>Adding Blog</button>}
            </form>
        </div>
     );
}

export default Create;
