import { useParams, useNavigate } from 'react-router-dom';
import useFetch from './useFetch';


const BlogDetails = () => {
    const { id } = useParams();
    const { data: recipe, error, isPending } =  useFetch('http://localhost:8000/blogs/' + id);
    const history = useNavigate();


    const handleClick = () => {
        fetch('http://localhost:8000/blogs/'+ recipe.id, {
            method: 'DELETE'
        }).then(() => {
            history.push('/');
        })
        window.location = '/';
    }

    return ( 
        <div className="blog-details">
            {isPending && <div>Loading...</div>}
            {error && <div>{error}</div>}
            <article >
                <img src={require(recipe.image)} ></img>

                <h2>{recipe.title}</h2>
                <p>Written by {recipe.author}</p>
                <div>{recipe.body}</div>
                <button onClick={handleClick}>Delete</button>
            </article>
        </div>
     );
}

export default BlogDetails;
